<script>
document.addEventListener('DOMContentLoaded', () => {
  document.querySelectorAll('section h2').forEach(title => {
    const content = Array.from(title.parentElement.children).filter(el => el.tagName !== 'H2');
    const wrapper = document.createElement('div');
    wrapper.classList.add('content');
    content.forEach(el => wrapper.appendChild(el));
    title.parentElement.appendChild(wrapper);

    title.addEventListener('click', () => {
      const section = title.parentElement;
      const open = section.classList.contains('open');
      document.querySelectorAll('section').forEach(s => s.classList.remove('open'));
      document.querySelectorAll('section h2').forEach(t => t.classList.remove('active'));
      if (!open) {
        section.classList.add('open');
        title.classList.add('active');
      }
    });
  });
});
</script>


