<!-- Petit script d'interaction pour basculer les sections (accodéon simple) -->
<script>
document.addEventListener('DOMContentLoaded', function(){
  document.querySelectorAll('[data-doc-section] .doc-section-title').forEach(function(title){
    title.addEventListener('click', function(){
      var section = title.closest('[data-doc-section]');
      var open = section.classList.contains('open');
      document.querySelectorAll('[data-doc-section]').forEach(function(s){ s.classList.remove('open'); });
      if(!open) section.classList.add('open');
      // scroll into view for small screens
      if(window.innerWidth < 800) section.scrollIntoView({behavior:'smooth',block:'start'});
    });
  });
});
</script>

